  };
  
  axios.request(options).then(function (response) {
    console.log(response.data);
  }).catch(function (error) {
    console.error(error);
  });
*/
const fs = require('fs');
const request = require('request');

const options = {
  method: 'POST',
  url: 'http://localhost:3001/imagem',
  headers: {'Content-Type': 'multipart/form-data; boundary=---011000010111000001101001'},
  formData: {
    file: {
      value: fs.createReadStream('C:\Users\Adriele\Downloads\image (1).jpg'),
      options: {filename: 'C:\Users\Adriele\Downloads\image (1).jpg', contentType: null}
    }
  }
};

request(options, function (error, response, body) {
  if (error) throw new Error(error);

  console.log(body);
});

}

    return (
    <form method="post" enctype="multipart/formdata">
    <label class="control-label">Select File</label>
    <input name="upl" id="input-1" type="file" id="file" onChange={(v) => console.log(v.target) }/>
    <input type="submit" value="submit" onClick={enviaDados}/>
</form>
  );
}
