import React, { useState } from "react";
import "./form.css";
import HeaderOne from "../header/header.js";
import axios, {post} from 'axios'


function Formimg() {
  let formData;
  const [imagem, setImagem] = useState(""); 
  
  const enviaDados = async (e) => {

    e.preventDefault(); 
    let formData = new FormData();
    formData.append('file', {
      file:imagem,
      name: 'file',
      type: 'image/jpg',
    });

    const requestConfig = {
      headers: {
        Accept: 'application/json',
        'Content-Type': 'multipart/form-data'
      }
    }   
    try {
        console.log(formData);
        const response = axios.post('/imagem', formData, requestConfig);
        console.log('Uri: '+response.data.uri);
        if(response.erro){
          throw new Error({response: 'Erro no Upload'});
        } else {
          return response.data.uri;
        }
      } catch (error) {
        console.log(error.response);
        return null;
      }
  }

  return (
    <form method="post" encType="multipart/form-data" action="/imagem">
        <input type="file" name="file"  onChange={(v) => setImagem(v)}></input>
        <button onClick={enviaDados} value="Enviar">
          Enviar{" "}
          </button>
    </form>
    )
}

export default Formimg;
