  let formData;
 
  const [imagem, setImagem] = useState("");
  const enviaDados = async (e) => {
    console.log('enviando');

    e.preventDefault(); 
    axios //faz chamada http
    .post('http://localhost:3001/imagem', 
    {
     file:imagem  // nome: nome
    })
      .then((response) => {
      })
      .catch((error) => {
        console.log('erro enviando formulario')
        console.log(error);
      });

  }

  return (
    <form method="post" encType="multipart/form-data" action="/imagem">
        <input type="file" name="file"  onChange={(v) => setImagem(v)}></input>
        <button onClick={enviaDados} value="Enviar">
          Enviar{" "}
          </button>
    </form>
    )
}

export default Formimg;
