import React, { useState } from "react";
import "./form.css";
import HeaderOne from "../header/header.js";
import axios, {post} from 'axios'


function Formimg() {
  let formData;
  const [imagem, setImagem] = useState(""); 
  
  const enviaDados = async (e) => {
    console.log(imagem);

    e.preventDefault(); 
    let formData = new FormData();
    formData.append('file', {
      imagem,
      name: 'file',
      type: `image/jpg`,
    });
    console.log('FormData: ',formData);
    const requestConfig = {
      headers: {
        Accept: 'application/json',
        'Content-Type': 'multipart/form-data'
      }
    }   

  }

  return (
    <form method="post" encType="multipart/form-data" action="/imagem">
        <input type="file" name="file"  onChange={(v) => setImagem(v)}></input>
        <button onClick={enviaDados} value="Enviar">
          Enviar{" "}
          </button>
    </form>
    )
}

export default Formimg;
