import React, { useState } from "react";
import "./form.css";
import HeaderOne from "../header/header.js";
import axios, {post} from 'axios'


function Formimg() {
  let formData;
  const [imagem, setImagem] = useState(""); 
  
  const enviaDados = async (e) => {

    e.preventDefault(); 
    var data = new FormData()
    var imagedata = document.querySelector('input[type="file"]').files[0];
    data.append("data", imagedata);

    fetch("http://localhost:3001/imagem", {
      mode: 'no-cors',
      method: "POST",
      body: data
    }).then(function (res) {
      if (res.ok) {
        alert("Perfect! ");
      } else if (res.status == 401) {
        alert("Oops! ");
      }
    }, function (e) {
      alert("Error submitting form!");
    });
  /*  formData.append('file', {
        uri,
        name: fileName,
        type: `image/${fileType}`,
      });
      console.log('FormData: ',formData);
      const requestConfig = {
        headers: {
          Accept: 'application/json',
          'Content-Type': 'multipart/form-data'
        }
      }
    }   
    try {
        console.log(formData);
        const response = axios.post('/imagem', formData, requestConfig);
        console.log('Uri: '+response.data.uri);
      } catch (error) {
        console.log(error.response);
        return null;
    }*/
  }

  return (
    <form method="post" encType="multipart/form-data" action="/imagem">
        <input type="file" name="file"  onChange={(v) => setImagem(v)}></input>
        <button onClick={enviaDados} value="Enviar">
          Enviar{" "}
          </button>
    </form>
    )
}

export default Formimg;
