import React, { useState } from "react";
import "./form.css";
import HeaderOne from "../header/header.js";
import axios, {post} from 'axios'
function Home() {
  let formData;
  const [tipo, setTipo] = useState("");
  const [imagem, setImagem] = useState("");
  const enviaDados = async (e) => {
    console.log('enviando');
    e.preventDefault(); 
    
    axios //faz chamada http
    .post('http://localhost:3001/questao', 
    {
      tipo,formData   // nome: nome
    })
      .then((response) => {
        
      
      })
      .catch((error) => {
        console.log('erro enviando formulario')
        console.log(error);
      });
    }

    const enviaImagem = async (e) => {
      console.log(imagem);
      e.preventDefault(); 
      
      axios //faz chamada http
      .post('http://localhost:3001/imagem', 
      {
      file: imagem // nome: nome
      })
        .then((response) => {
          
        
        })
        .catch((error) => {
          console.log('erro enviando formulario')
          console.log(error);
        });
      }
    function onChange(e){
   let files=e.target.files;
  let reader= new FileReader();
  reader.readAsDataURL(files[0]);    
  reader.onload =(e) =>{
    console.warn(e.target.result)
    const url="http://localhost:3001/"
    formData={file: e.target.result}
  //  var a = document.createElement("a"); //Create <a>
   // a.href = "data:image/png;base64," +e.target.result //Image Base64 Goes here
    //a.download = "Image.png"; //File name Here
  //  a.click(); //Downloaded file
  }

  var link = document.createElement('a');
//link.href = e;
//link.download = 'Download.jpg';
//document.body.appendChild(link);
//link.click();
//document.body.removeChild(link);

}

  return (
    <div>
      <HeaderOne one="-"></HeaderOne>
      <br />
      <br />
      <div className="topic">
        <h1>Escolha o tipo de questão</h1>
        <br />
        <div>
          <input
            type="radio"
            className="Tipo"
            name="AC"
            onChange={(v) => setTipo('AC')}
            value={tipo}
            onClick={() => desmarcar(0)}
          />{" "}
          Associar Colunas
          <br />
          <input
            type="radio"
            className="Tipo"
            name="OP"
            onChange={(v) => setTipo('OP')}
            value={tipo}
            onClick={() => desmarcar(1)}
          />
          Ordenar palavras de uma frase
          <br />
          <input
            type="radio"
            className="Tipo"
            name="DPV"
            onChange={(v) => setTipo('DPV')}
            value={tipo}
            onClick={() => desmarcar(2)}
          />
          Digitar palavra da imagem
          <br />
          <input
            type="radio"
            className="Tipo"
            name="OP"
            onChange={(v) => setTipo('OP')}
            value={tipo}
            onClick={() => desmarcar(3)}
          />
          Digitar palavra do vídeo
          <br />
          <input
            type="radio"
            className="Tipo"
            name="DPF"
            onChange={(v) => setTipo('DPF')}
            value={tipo}
            onClick={() => desmarcar(4)}
          />
          Digitar frase de um vídeo em libras
          <br />
          <input
            type="radio"
            className="Tipo"
            name="PLFD"
            onChange={(v) => setTipo('PLFD')}
            value={tipo}
            onClick={() => desmarcar(5)}
          />
          Preencher lacuna da frase digitando
