    console.log('enviando');
    e.preventDefault(); 
    axios.post('http://localhost:3001/imagem', {file: file}, requestConfig);
   
    }
    

  return (
    <form class="uploadForm" method="post" enctype="multipart/formdata">
    <label class="control-label">Select File</label>
    <input name="upl" id="input-1" type="file" class="file" onChange={(v) => setFile(v.target.files[0]) }/>
