const enviaDados = async (e) => {
  e.preventDefault()
  var axios = require("axios").default;
/*
  var options = {
    method: 'POST',
    url: 'http://localhost:3001/imagem',
    headers: {'Content-Type': 'multipart/form-data; boundary=---011000010111000001101001'},
    data: '-----011000010111000001101001\r\nContent-Disposition: form-data; name="file"; filename="image (1).jpg"\r\nContent-Type: image/jpeg\r\n\r\n\r\n-----011000010111000001101001--\r\n'
  };
  
  axios.request(options).then(function (response) {
    console.log(response.data);
  }).catch(function (error) {
    console.error(error);
  });
*/
const http = require("http");

const options = {
  "method": "POST",
  "hostname": "localhost",
  "port": "3001",
  "path": "/imagem",
  "headers": {
    "Content-Type": "multipart/form-data; boundary=---011000010111000001101001",
    "Content-Length": "0"
  }
};

const req = http.request(options, function (res) {
  const chunks = [];

  res.on("data", function (chunk) {
    chunks.push(chunk);
  });

  res.on("end", function () {
    const body = Buffer.concat(chunks);
    console.log(body.toString());
  });
});

req.write("-----011000010111000001101001\r\nContent-Disposition: form-data; name=\"file\"; filename=\"image (1).jpg\"\r\nContent-Type: image/jpeg\r\n\r\n\r\n-----011000010111000001101001--\r\n");
req.end();
}

    return (
    <form method="post" enctype="multipart/formdata">
    <label class="control-label">Select File</label>
    <input name="upl" id="input-1" type="file" id="file" onChange={(v) => console.log(v.target) }/>
    <input type="submit" value="submit" onClick={enviaDados}/>
</form>
  );
}
