import React, {useState} from "react";
import "./login.css";
import HeaderOne from '../header/header.js'
import axios, {post} from 'axios'
let token=""
export default function Login(){
  const [email, setEmail] = useState('');
  const [senha, setSenha] = useState('');
  const enviaDados = async (e) => {
   
    e.preventDefault(); 

axios.post('http://localhost:3001/login', 
{
  email,
 senha // nome: nome
}).then((response) => {
   token = response.data.token;
   let emailLogado = response.data.email;
   console.log(token)

localStorage.setItem("token",token);
localStorage.setItem("user",emailLogado);
   
  alert('Autenticado');
 
 window.location.href = 'http://localhost:3000/logged';
  }, (error) => {
   alert("Não autenticado")
    console.log(error);
  });
    
     
}


    return(
    <div>
        <HeaderOne pagLogin={true} login={false}></HeaderOne>
        
        
  <br></br>
        <div id="inputs">
        <h1>Login</h1>
        <br/>
        <input
            type="email"
            className="email"
            name="email"
            onChange={(v) => setEmail(v.target.value)}
            value={email} placeholder="Email"/>
          <br />
          <br />
        <input
            type="password"
            className="password"
            name="password"
            onChange={(v) => setSenha(v.target.value)}
            value={senha} placeholder="Senha"/>
          <br />
          <br />
          <button onClick={enviaDados} value="Enviar" className="forms-button">
          Enviar{" "}
        </button>

        </div>
        </div>
    )
}
