import React, { useState } from "react";
import axios, {post} from 'axios'

import Dropzone from "react-dropzone";

export default function App() {
  const [file, setFile] = useState([]);
/*
  const enviaDados = async (e) => {
    console.log('enviando');
    e.preventDefault(); 
    const requestConfig = {
      method: 'POST',
      headers: {
        Accept: 'application/json',
        'Content-Type': 'multipart/form-data'
      }
    }
    axios.post('http://localhost:3001/imagem', {file: file}, requestConfig);
   
    }
    
    import axios from "axios";
*/


const enviaDados = async (e) => {
  e.preventDefault()
  const fs = require('fs');
  const FormData = require('form-data');
  const fetch = require('node-fetch');
  const formData = new FormData();
  
  formData.append('file', fs.createReadStream('C:\Users\Adriele\Downloads\image (1).jpg'));
  
  let url = 'http://localhost:3001/imagem';
  
  let options = {
    method: 'POST',
    headers: {'Content-Type': 'multipart/form-data; boundary=---011000010111000001101001'}
  };
  
  options.body = formData;
  
  fetch(url, options)
    .then(res => res.json())
    .then(json => console.log(json))
    .catch(err => console.error('error:' + err));
}
    return (
    <form method="post" enctype="multipart/formdata">
    <label class="control-label">Select File</label>
    <input name="upl" id="input-1" type="file" class="file" onChange={(v) => setFile(v.target.files) }/>
    <input type="submit" value="submit" onClick={enviaDados}/>
</form>
  );
}
